{% load staticfiles vv_tags %}

<div id="topbar">{% include "topbar.html" %}</div>
<div style="display:inline-block;position:relative;top:11px;left:2em;font-size:280%;color:white">
	<span class="hidden-xxs">
		<a href="/">Django Mogo</a>
	</span>
</div>
{% if "presence"|is_installed %}
	<div style="display:inline-block;position:relative;top:2.2em;left:1.2em">{% include "presence/widget.html" %}</div>
{% endif %}

{% load vv_tags mbase %}
<script type="text/javascript">
	{% if request.path|in_admin %}
		window.setTimeout(function() { django.jQuery(".messagelist .success").fadeOut('slow'); }, 2500);
		window.setTimeout(function() { django.jQuery(".messagelist .info").fadeOut('slow'); }, 4000);
	{% else %}
		window.setTimeout(function() { $(".alert-success").fadeOut('slow'); }, 2500);
		window.setTimeout(function() { $(".alert-info").fadeOut('slow'); }, 4000);
	{% endif %}
</script>
{% if "instant"|is_installed %}
	{% include "instant/client.html" %}
{% endif %}

{% load i18n vv_tags %}
<ul class="list-inline gi">
	{% if user.is_staff %}
	<li>
		<a class="btn btn-link{% if request.path|slice:'6' == '/admin' %} active{% endif %}" href="/admin/">Admin</a>
	</li>
	{% endif %}
	{% if "vvpages"|is_installed %}
		{% include "vvpages/auto/navlinks.html" %}
		<li>
			<a class="btn btn-link" href="/map/">Sitemap</a>
		</li>
	{% endif %}
	{% if "vvcontact"|is_installed %}
	<li>
		<a class="btn btn-link" href="/contact/">Contact</a>
	</li>
	{% endif %}
	{% if "vvcatalog"|is_installed %}
	<li>
		<a class="btn btn-link" href="{% url 'category-index' %}">Catalog</a>
	</li>
	{% endif %}
</ul>
{% if "instant"|is_installed and user.is_superuser %}
	<div class="pull-right" style="margin-top:0.7em;">
		{% include "vvinstant/btn.html" %}
	</div>
{% endif %}
<div class="pull-right" style="margin-top:0.7em;">
	<a href="#" @click="toggleSidebar()">Sidebar</a>
</div>

{% load i18n %}

<div class="gp text-center">
	{% if user.is_anonymous %}
		<div class="blocknav gbf" style="background-color:white;">
			<a href="/account/login/">
				<i class="fa fa-user" style="font-size:400%;padding:0.5em;"></i><br />
				{% trans 'Login or register' %}
			</a>
		</div>
	{% else %}
		<div class="blocknav gbf" style="background-color:white;">
			<a href="/account/logout/">
				<i class="fa fa-sign-out" style="font-size:400%;padding:0.5em;"></i><br />
				{% trans 'Logout' %}
			</a>
		</div>
	{% endif %}
</div>

{% load staticfiles mbase compress %}

<meta http-equiv="X-UA-Compatible" content="IE=edge" />
<meta name="viewport" content="width=device-width, initial-scale=1.0" />
<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />
{% compress js %}
	{% if not request.path|in_admin %}
		<script type="text/javascript" src="{% static 'js/turbolinks.js' %}"></script>
	{% endif %}
{% endcompress %}
{% compress css %}
	{% if not request.path|in_admin %}
		<link rel="stylesheet" media="screen, projection" href="{% static 'font-awesome/css/font-awesome.min.css' %}" />
		<link rel="stylesheet" media="screen, projection" href="{% static 'css/picnic.min.css' %}" />
	{% endif %}
	<link rel="stylesheet" media="screen, projection" href="{% static 'css/screen.css' %}" />
	<link rel="stylesheet" media="screen, projection" href="{% static 'css/mbase.css' %}" />
	{% block extra_css %}{% endblock %}
{% endcompress %}

/* ##################################### Base tags ##################################### */
body  {
	background-color:#fff;
    margin: 0;
    padding: 0; 
}
a, a:hover, a:visited {
	text-decoration:none !important;
}
a:not(.btn), a:visited:not(.btn) {
	color:darkslategrey;
}
a:hover:not(.btn) {
	color:grey;
}
h1, h2, h3, h4, h5 {
	color:darkslategrey;
}
img {
	vertical-align:middle;
}
div {
	box-sizing: border-box;
	vertical-align:top;
}
/* ##################################### Skeleton ##################################### */
#site_header {
	min-height:50px;
	background-color:steelblue;
}
#topbar {
	position:absolute;
	top:1em;
	right:1em;
}
#site_header a, #site_header a:visited, #navbar a, #navbar a:visited {
	color:white !important;
	padding-right:0.8em;
}
#topbar a:hover {
	color:grey !important;
}

#navbar .list-inline a:hover, #navbar .list-inline a.active {
	color:darkslategrey !important;
	background-color:white;
}
#navbar {
	background-color:grey;
	padding:0 0 0 6em;
	height:2.9em;
	line-height:2.9em;
}
#navbar .btn {
	padding:0.9em 0.4em 0.9em 0.4em;
	vertical-align:baseline;
}
#content {
	min-height:40em;
	padding:0.5em 1em 4em 1em;
}
#footer {
	min-height:50px;
	background-color:steelblue;
}
