{% load mbase %}
<script type="text/javascript">
	{% if request.path|in_admin %}
		window.setTimeout(function() { django.jQuery(".messagelist .success").fadeOut('slow'); }, 2500);
		window.setTimeout(function() { django.jQuery(".messagelist .info").fadeOut('slow'); }, 4000);
	{% endif %}
	{% include "autoreload/client.js" %}
</script>
{% if "instant"|is_installed %}
	{% include "instant/client.html" %}
{% endif %}

{% load staticfiles mbase compress %}

<meta http-equiv="X-UA-Compatible" content="IE=edge" />
<meta name="viewport" content="width=device-width, initial-scale=1.0" />
<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />
{% compress js %}
	{% if not request.path|in_admin %}
		<script type="text/javascript" src="{% static 'js/turbolinks.js' %}"></script>
	{% endif %}
{% endcompress %}
{% compress css %}
	{% if not request.path|in_admin %}
	<link rel="stylesheet" media="screen, projection" href="{% static 'css/picnic.min.css' %}" />
	{% endif %}
	<link rel="stylesheet" media="screen, projection" href="{% static 'css/screen.css' %}" />
	<link rel="stylesheet" media="screen, projection" href="{% static 'css/mbase.css' %}" />
	{% block extra_css %}{% endblock %}
{% endcompress %}

{% load i18n %}

<div class="gp text-center">
	{% if user.is_anonymous %}
		<div class="blocknav gbf" style="background-color:white;">
			<a href="/account/login/">
				<i class="fa fa-user" style="font-size:400%;padding:0.5em;"></i><br />
				{% trans 'Login or register' %}
			</a>
		</div>
	{% else %}
		<div class="blocknav gbf" style="background-color:white;">
			<a href="/account/logout/">
				<i class="fa fa-sign-out" style="font-size:400%;padding:0.5em;"></i><br />
				{% trans 'Logout' %}
			</a>
		</div>
	{% endif %}
</div>

{% load i18n mbase %}
<div>
{% if perms.alapage.change_page %}
	<div class="grs" style="position:relative; float:right">
		<a href="/admin/alapage/page/{{ page.id }}/change/">
			<i class="demo-icon icon-edit-alt gbf">&#xe808;</i>&nbsp;{% trans 'Edit' %}
		</a>
	</div>
{% endif %}
<ul class="list-inline gfl gfw-w center-items" style="margin:0px; padding:0px;list-style-type:none">
		{% if user.is_staff %}
		<li>
			<a class="btn btn-link{% if request.path|slice:'6' == '/admin' %} active{% endif %}" href="/admin/">Admin</a>
		</li>
		{% endif %}
		<li>
			<a class="btn btn-link" href="/contact/">Contact</a>
		</li>
</ul>
</div>

/* ##################################### Base tags ##################################### */
body  {
	background-color:#fff;
    margin: 0;
    padding: 0; 
}
a, a:hover, a:visited {
	text-decoration:none !important;
}
a:not(.btn), a:visited:not(.btn) {
	color:darkslategrey;
}
a:hover:not(.btn) {
	color:grey;
}
h1, h2, h3, h4, h5 {
	color:darkslategrey;
}
img {
	vertical-align:middle;
}
div {
	box-sizing: border-box;
	vertical-align:top;
}
/* ##################################### Icons ##################################### */
.i-code {
  display: none;
}
@font-face {
	  font-family: 'fontello';
	  src: url('/static/icon/font/fontello.eot?61793101');
	  src: url('/static/icon/font/fontello.eot?61793101#iefix') format('embedded-opentype'),
		   url('/static/icon/font/fontello.woff?61793101') format('woff'),
		   url('/static/icon/font/fontello.ttf?61793101') format('truetype'),
		   url('/static/icon/font/fontello.svg?61793101#fontello') format('svg');
	  font-weight: normal;
	  font-style: normal;
	}
.demo-icon {
  font-family: "fontello";
  font-style: normal;
  font-weight: normal;
  speak: none;
  display: inline-block;
  text-decoration: inherit;
  text-align: center;
  /* opacity: .8; */
}
/* ##################################### Skeleton ##################################### */
#site_header {
	min-height:75px;
	background-color:steelblue;
}
#topbar {
	position:absolute;
	top:0.5em;
	right:1em;
}
#site_header a, #site_header a:visited, #navbar a, #navbar a:visited {
	color:white !important;
	padding-right:0.8em;
}
#topbar a:hover {
	color:grey !important;
}

#navbar .list-inline a:hover, #navbar .list-inline a.active {
	color:darkslategrey !important;
	background-color:white;
}
#navbar {
	background-color:grey;
	padding:0em 0em 0em 2em;
	height:2.9em;
	line-height:2.9em;
}
#navbar .btn {
	padding:0.9em 0.4em 0.9em 0.4em;
	vertical-align:baseline;
}
#content {
	min-height:40em;
	padding:0.5em 1em 4em 1em;
}
#footer {
	min-height:50px;
	background-color:steelblue;
}
