{% load mbase %}
<script type="text/javascript">
	{% if request.path|in_admin %}
		window.setTimeout(function() { django.jQuery(".messagelist .success").fadeOut('slow'); }, 2500);
		window.setTimeout(function() { django.jQuery(".messagelist .info").fadeOut('slow'); }, 4000);
	{% endif %}
</script>
{% if "instant"|is_installed %}
	{% include "instant/client.html" %}
{% endif %}

{% load staticfiles mbase compress %}

<meta http-equiv="X-UA-Compatible" content="IE=edge" />
<meta name="viewport" content="width=device-width, initial-scale=1.0" />
<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />
{% compress js %}
	{% if not request.path|in_admin %}
		<script type="text/javascript" src="{% static 'js/turbolinks.js' %}"></script>
	{% endif %}
{% endcompress %}
{% compress css %}
	{% if not request.path|in_admin %}
		<link rel="stylesheet" media="screen, projection" href="{% static 'font-awesome/css/font-awesome.min.css' %}" />
		<link rel="stylesheet" media="screen, projection" href="{% static 'css/picnic.min.css' %}" />
	{% endif %}
	<link rel="stylesheet" media="screen, projection" href="{% static 'css/screen.css' %}" />
	<link rel="stylesheet" media="screen, projection" href="{% static 'css/mbase.css' %}" />
	{% block extra_css %}{% endblock %}
{% endcompress %}

{% load i18n %}
{% with request.path|slice:":6" as path %}
	{% if user.is_staff %}
		{% if path != '/admin' %} 
			<a href="/admin/" class="hidden-xxs" title="Admin">
				<i class="fa fa-cogs gbf-xl"></i>&nbsp;
			</a>
		{% endif %}
	{% endif %}
	{% if user.is_anonymous %}
	<a href="/account/login/" class="hidden-xxs" title="{% trans 'Login' %}">
		<i class="fa fa-user gbf-xl"></i>&nbsp;
	</a>
	{% else %}
	<a href="/account/logout/" class="hidden-xxs" title="{% trans 'Logout' %}">
		<i class="fa fa-sign-out gbf-xl"></i>&nbsp;
	</a>
	{% endif %}
	{% if path != '/admin' %} 
		<script type="text/javascript">
		function menu() {
			var menu = document.getElementById('popup_menu');
			if (menu.style.display === "none") {
				menu.style.display = "block";
			} else {
				menu.style.display = "none";
			}
		}
		</script>
		<a href="javascript:menu()" title="Menu">
			<i class="fa fa-navicon gbf-xl"></i>&nbsp;
		</a>
	{% endif %}
{% endwith %}

{% load i18n %}

<div class="gp text-center">
	{% if user.is_anonymous %}
		<div class="blocknav gbf" style="background-color:white;">
			<a href="/account/login/">
				<i class="fa fa-user" style="font-size:400%;padding:0.5em;"></i><br />
				{% trans 'Login or register' %}
			</a>
		</div>
	{% else %}
		<div class="blocknav gbf" style="background-color:white;">
			<a href="/account/logout/">
				<i class="fa fa-sign-out" style="font-size:400%;padding:0.5em;"></i><br />
				{% trans 'Logout' %}
			</a>
		</div>
	{% endif %}
</div>

{% load i18n mbase %}
{% if perms.alapage.change_page %}
	<div class="pull-right">
		<a href="/admin/alapage/page/{{ page.id }}/change/">
			<i class="fa fa-edit"></i>&nbsp;{% trans 'Edit' %}
		</a>
	</div>
{% endif %}
{% if perms.alapage.create_page %}
	<div class="pull-right" v-show="adminPageUrl">
		<a href="/wizard/">
			<i class="fa fa-edit"></i>&nbsp;{% trans 'Wizard' %}
		</a>
	</div>
{% endif %}
<ul class="list-inline gfl gfw-w center-items" style="margin:0px; list-style-type:none">
		{% if user.is_staff %}
		<li style="">
			<a class="btn btn-link{% if request.path|slice:'6' == '/admin' %} active{% endif %}" href="/admin/">Admin</a>
		</li>
		{% endif %}
		<li>
			<a class="btn btn-link" href="/contact/">Contact</a>
		</li>
</ul>

/* ##################################### Base tags ##################################### */
body  {
	background-color:#fff;
    margin: 0;
    padding: 0; 
}
a, a:hover, a:visited {
	text-decoration:none !important;
}
a:not(.btn), a:visited:not(.btn) {
	color:darkslategrey;
}
a:hover:not(.btn) {
	color:grey;
}
h1, h2, h3, h4, h5 {
	color:darkslategrey;
}
img {
	vertical-align:middle;
}
div {
	box-sizing: border-box;
	vertical-align:top;
}
/* ##################################### Skeleton ##################################### */
#site_header {
	min-height:50px;
	background-color:steelblue;
}
#topbar {
	position:absolute;
	top:1em;
	right:1em;
}
#site_header a, #site_header a:visited, #navbar a, #navbar a:visited {
	color:white !important;
	padding-right:0.8em;
}
#topbar a:hover {
	color:grey !important;
}

#navbar .list-inline a:hover, #navbar .list-inline a.active {
	color:darkslategrey !important;
	background-color:white;
}
#navbar {
	background-color:grey;
	padding:0 0 0 6em;
	height:2.9em;
	line-height:2.9em;
}
#navbar .btn {
	padding:0.9em 0.4em 0.9em 0.4em;
	vertical-align:baseline;
}
#content {
	min-height:40em;
	padding:0.5em 1em 4em 1em;
}
#footer {
	min-height:50px;
	background-color:steelblue;
}
